<script>
  window.addEventListener("DOMContentLoaded", () => {
    const sidebarElement = document.getElementById('sidebar');
    const sidebarButtons = Array.from(sidebarElement.getElementsByTagName('button'));
    sidebarButtons.map((button, index) => {
      button.addEventListener("click", () => {
        const dropdown = sidebarElement.querySelector(`#dropdown-${button.id}`);
        const btnDropIcon = sidebarElement.querySelector(`#btn-drop-icon-${button.id}`);
        if (dropdown.className === 'hidden') {
          dropdown.classList.remove('hidden');
          btnDropIcon.classList.remove("px-2");
          btnDropIcon.classList.add("px-[10.5px]");
          btnDropIcon.innerHTML = "-";
        } else {
          dropdown.classList.add('hidden');
          btnDropIcon.classList.remove("px-[10.5px]");
          btnDropIcon.classList.add("px-2");
          btnDropIcon.innerHTML = "+";
        }
      });
    });
  });
</script>